mapper.insert:
insert into blog_tags (id, tag_id, blog_id
  )
values (#{id,jdbcType=INTEGER}, #{tag_id,jdbcType=INTEGER}, #{blog_id,jdbcType=INTEGER}
  )

mapper.update:
