set tag_id = #{tag_id,jdbcType=INTEGER},
  blog_id = #{blog_id,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, tag_id, blog_id
from blog_tags
where id = #{id,jdbcType=INTEGER}

select id, tag_id, blog_id
from blog_tags
where blog_id = #{id,jdbcType=INTEGER}
