mapper.update:
update user
set `name` = #{name,jdbcType=VARCHAR},
  birthday = #{birthday,jdbcType=VARCHAR},
  area = #{area,jdbcType=VARCHAR},
  job = #{job,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, `name`, birthday, area, job
from user
where id = #{id,jdbcType=INTEGER}
