mapper.select:
select b.id, c.category_name categoryName , b.category_id,b.title, b.summary, b.img_url, b.author, b.code, b.view_count, b.like_count, b.comment_count, b.is_deleted, b.create_time, b.update_time, b.content,tag.tag_name,b.html_content from blog b left join blog_tags t on b.id = t.blog_id left join category c on b.category_id = c.id left join tag on t.tag_id = tag.id
and t.tag_id = #{search}
order by b.id desc

select b.id, c.category_name categoryName ,b.category_id, b.title, b.summary, b.img_url, b.author, b.code, b.view_count, b.like_count, b.comment_count, b.is_deleted, b.create_time, b.update_time, b.content,b.html_content from blog b left join category c on b.category_id = c.id
and b.category_id = #{search}
order by b.id desc
